reading_passage = input("Please enter a reading passage or academic text: ")

# Step 3: Scan the input and check for matches
# Convert the input to a list of words (split by whitespace)
words_in_passage = reading_passage.split()

# Initialize sets to store matching words from each list
matches_NAWL = set()
matches_NGSL = set()
#matches_list3 = set()

# Check for matches in each list
for word in words_in_passage:
    if word in NAWL:
        matches_NAWL.add(word)
    if word in NGSL:
        matches_NGSL.add(word)
    #if word in list3:
        #matches_list3.add(word)

# Step 4: Output the matching words to the user
print("")
if matches_NAWL:
    print(f"Words from NAWL that match: {', '.join(matches_NAWL)}")
    print("")
else:
    print("No words from NAWL matched.")
    print("")

if matches_NGSL:
    print(f"Words from NGSL that match: {', '.join(matches_NGSL)}")
    print("")
else:
    print("No words from NGSL matched.")
